mapper.select:
    SELECT
	    ST_AsMVT(tile, ${layerName}, 4096, 'geoms') img
    FROM
	    (SELECT ST_AsMVTGeom(${geomName}, ST_MakeEnvelope(#{lonMin, jdbcType=DOUBLE}, #{latMin, jdbcType=DOUBLE}, #{lonMax, jdbcType=DOUBLE}, #{latMax, jdbcType=DOUBLE}, 4326)) AS geoms
	    FROM public.${tableName}
	    WHERE st_intersects(${geomName}, ST_MakeEnvelope(#{lonMin, jdbcType=DOUBLE}, #{latMin, jdbcType=DOUBLE}, #{lonMax, jdbcType=DOUBLE}, #{latMax, jdbcType=DOUBLE}, 4326))) AS tile;
